    private void formInternalFrameOpened(javax.swing.event.InternalFrameEvent evt) {//GEN-FIRST:event_formInternalFrameOpened
        try {
            // TODO add your handling code here:
            con=Connect.seconnenter();
             Statement s=con.createStatement();
            ResultSet rs=s.executeQuery("select *from client ");
            while(rs.next()){
             // clients.add(new Client(rs.getInt(1),rs.getString(2),rs.getString(3),rs.getString(4),rs.getString(5),rs.getInt(6)));
            df.addRow(new Object[]{rs.getInt(1),rs.getString(2),rs.getString(3),rs.getString(4),rs.getInt(5)});
            }
        } catch (Exception ex) {
            Logger.getLogger(JICLient2.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_formInternalFrameOpened

    private void btnAjouterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAjouterActionPerformed
        try {
            int id=0;
            PreparedStatement ps=con.prepareStatement("insert into client values(seq_idclt.nextval,?,?,?,?)",new int []{1});
            ps.setString(1, txtNom.getText());
            ps.setString(2, txtMail.getText());
            ps.setString(3, txtTel.getText());
            ps.setInt(4, Integer.parseInt(txtice.getText()));
            ps.executeUpdate();
            ResultSet rs=ps.getGeneratedKeys();
            if(rs.next()){
                id=rs.getInt(1);
            }
            df.addRow(new Object[]{id,txtNom.getText(),txtMail.getText(),txtTel.getText(),Integer.parseInt(txtice.getText())});
            JOptionPane.showMessageDialog(this, "CLient Ajouter avec Succes");
            clear();
          } catch (SQLException ex) {
              JOptionPane.showMessageDialog(this, "Eroror de ");
            Logger.getLogger(JICLient2.class.getName()).log(Level.SEVERE, null, ex);
        }

    }//GEN-LAST:event_btnAjouterActionPerformed

    private void btnmodifeirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnmodifeirActionPerformed
        try {
            // TODO add your handling code here:
            int row=Tableclient.getSelectedRow();
           // Client c=clients.get(pos);
            int id_art=Integer.parseInt(df.getValueAt(row,0).toString());
            String nom=txtNom.getText();
           // String prenom=txtPrenom.getText();
            String mail=txtMail.getText();
            String tel=txtTel.getText();
            int ice=Integer.parseInt(txtice.getText());
            con=Connect.seconnenter();
            PreparedStatement ps=con.prepareStatement("update client set nom=?,mail=?,tel=?,ice=? where idclt=?");
            ps.setString(1, nom);
           // ps.setString(2, prenom);
            ps.setString(2, mail);
            ps.setString(3, tel);
            ps.setInt(4, ice);
            ps.setInt(5, id_art);
            ps.executeUpdate();
            /*c.setNom(nom);
            c.setPrenom(prenom);
            c.setMail(mail);
            c.setIce(ice);
            c.setTel(tel);*/
            df.setValueAt(nom,row,1);
            //df.setValueAt(prenom,row,2);
            df.setValueAt(mail,row,2);
            df.setValueAt(tel,row,3);
            df.setValueAt(ice,row,4);
            JOptionPane.showMessageDialog(this, "Client Modifier Avec Succes");
             clear();
        } catch (Exception ex) {
            Logger.getLogger(JICLient2.class.getName()).log(Level.SEVERE, null, ex);
            JOptionPane.showMessageDialog(this, "Eroor de l'operation");
        }
    }//GEN-LAST:event_btnmodifeirActionPerformed

    private void btnSupprimerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSupprimerActionPerformed
        try {
            // TODO add your handling code here:
            //Client c=clients.get(pos);
            int row=Tableclient.getSelectedRow();
            con=Connect.seconnenter();
            PreparedStatement ps=con.prepareStatement("delete from client where idclt=?");
            ps.setInt(1,Integer.parseInt(df.getValueAt(row,0).toString()));
            ps.executeUpdate();
            df.removeRow(row);
         clear();
            JOptionPane.showMessageDialog(this, "Client Supprimer Avec Succes");
        } catch (Exception ex) {
            Logger.getLogger(JICLient2.class.getName()).log(Level.SEVERE, null, ex);
            JOptionPane.showMessageDialog(this, "Eroor de l'operation");
        }

    }//GEN-LAST:event_btnSupprimerActionPerformed

