    private void formInternalFrameOpened(javax.swing.event.InternalFrameEvent evt) {//GEN-FIRST:event_formInternalFrameOpened
        try {
            // TODO add your handling code here:
            Connection con=Connect.seconnenter();
            Statement s=con.createStatement();
            ResultSet rs=s.executeQuery("select *from Client");
            while(rs.next()){
                cbxClient.addItem(new Client(rs.getInt(1),rs.getString(2),rs.getString(3),rs.getString(4),rs.getInt(5)));
            }
            Statement st=con.createStatement();
            ResultSet sr=st.executeQuery("select *from Article where qtestock>0");
            while(sr.next()){
                cbxArticle.addItem(new Article(sr.getInt(1),sr.getString(2),sr.getInt(3)));
            }
        } catch (Exception ex) {
            Logger.getLogger(JiCommande.class.getName()).log(Level.SEVERE, null, ex);
        }
        
    }//GEN-LAST:event_formInternalFrameOpened

