    private void BtnAjouterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnAjouterActionPerformed
        try {
            // TODO add your handling code here:
            Article a=(Article) cbxArticle.getSelectedItem();
            Connection con=Connect.seconnenter();
            float prix=Float.parseFloat(txtprix.getText());
            int qte=Integer.parseInt(txtQte.getText());
            CallableStatement cs=con.prepareCall("{ call ?:=get_qte(?) }");
            cs.setInt(2,a.getId_art());
            cs.registerOutParameter(1,java.sql.Types.INTEGER);
            cs.execute();
            int qte_stock=cs.getInt(1);
            if(qte_stock>=qte){
            sousTotal=prix*qte;
            df.addRow(new Object[]{a.getId_art(),a.getLibelle(),prix,qte,sousTotal});
            total+=sousTotal;
            labTHT.setText(total+"");
            labTva.setText(total*0.2+"");
            labttc.setText(total*1.2+"");
            txtprix.setText("");
            txtQte.setText("");
            }else{
                JOptionPane.showMessageDialog(this," invalid Qte dans le stock ");
            }
           
        } catch (Exception ex) {
            Logger.getLogger(JiCommande.class.getName()).log(Level.SEVERE, null, ex);
        }
        
    }//GEN-LAST:event_BtnAjouterActionPerformed

    private void btnEnregisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnregisterActionPerformed
        try {
            // TODO add your handling code here:
            Client c=(Client) cbxClient.getSelectedItem();
            Article a=(Article) cbxArticle.getSelectedItem();
            int nocmd =Integer.parseInt(txtNum.getText());
            Connection con=Connect.seconnenter();
            //Start  commande
            PreparedStatement ps=con.prepareStatement("insert into commande(nocmd,idclt,datecmd) values(?,?,sysdate)");
            ps.setInt(1,nocmd);
            ps.setInt(2,c.getId_clt());
            ps.executeUpdate();
            //end Commande
            //End lignecommande
            PreparedStatement st=con.prepareStatement("insert into lignecommande values(seq_nolc.nextval,?,?,?,?)");
            st.setInt(1, nocmd);
            int n=jTable1.getRowCount();
            for(int i=0;i<n;i++){
                st.setInt(2,Integer.parseInt(df.getValueAt(i,0).toString()));
                st.setFloat(3,Float.parseFloat(df.getValueAt(i,2).toString()));
                st.setInt(4,Integer.parseInt(df.getValueAt(i,3).toString()));
                st.executeUpdate();
            }
            //end LigneCommande
            // Start Paiment 
            PreparedStatement z=con.prepareStatement("insert into paiment values(seq_idp.nextval,?,?,?)");
            z.setInt(1,c.getId_clt());
            z.setFloat(2,Float.parseFloat(labttc.getText()));
            z.setFloat(3,0);
            z.executeUpdate();
            //end Paiment
            df.setRowCount(0);
            labTHT.setText("");
            labTva.setText("");
            labttc.setText("");
            JOptionPane.showMessageDialog(this,"Commande enregistrer");
            int choix=JOptionPane.showConfirmDialog(this,"vouler vous imprimer","Confirmation",JOptionPane.YES_NO_OPTION);
            if(choix==JOptionPane.YES_OPTION){
            JasperDesign jasperdesign=JRXmlLoader.load("src//Report//ReportCommande.jrxml");
            JasperReport jasperReport=JasperCompileManager.compileReport(jasperdesign);
            Map parametres =new HashMap();
            parametres.put("nocmd", nocmd);
            JasperPrint jasperprint=JasperFillManager.fillReport(jasperReport, parametres,Connect.seconnenter());
            JRViewer jv=new JRViewer(jasperprint);
            ImpressionFrame v=new ImpressionFrame();
            v.setVisible(true);
            v.getContentPane().add(jv);
                //JasperExportManager.exportReportToPdfFile(jasperprint,"src\\bibliotheque"+"pdf");
            }
        } catch (Exception ex) {
            Logger.getLogger(JiCommande.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btnEnregisterActionPerformed

    private void BtnSupprimerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnSupprimerActionPerformed
        // TODO add your handling code here:
        int choix=JOptionPane.showConfirmDialog(this,"Voulez vous Retirer Ce Article","Confirmation",JOptionPane.YES_NO_OPTION);
        if(choix==JOptionPane.YES_OPTION){
        float t=Float.parseFloat(labTHT.getText());
        int row=jTable1.getSelectedRow();
        float stotal=Float.parseFloat(df.getValueAt(row, 4).toString());
        float v=0;
        df.removeRow(row);
        if(t>stotal){
             total=t-stotal;
            labTHT.setText(total+"");
            labTva.setText(total*0.2+"");
            labttc.setText(total*1.2+"");
        }else if(t<stotal){
            total=stotal-t;
            labTHT.setText(total+"");
            labTva.setText(total+"");
            labttc.setText(total+"");
        }
        }
       
        
    }//GEN-LAST:event_BtnSupprimerActionPerformed

    private void formInternalFrameClosing(javax.swing.event.InternalFrameEvent evt) {//GEN-FIRST:event_formInternalFrameClosing
        // TODO add your handling code here:
        if(df.getRowCount()>0){
            int choix=JOptionPane.showConfirmDialog(this,"Voulez vous Quittez", "Confirmation",JOptionPane.YES_NO_OPTION);
            if(choix==JOptionPane.YES_OPTION){
                System.exit(0);
            }
        }
    }//GEN-LAST:event_formInternalFrameClosing


