    private void formInternalFrameOpened(javax.swing.event.InternalFrameEvent evt) {//GEN-FIRST:event_formInternalFrameOpened
        try {
            // TODO add your handling code here:
            Connection con=Connect.seconnenter();
            Statement s=con.createStatement();
            ResultSet rs=s.executeQuery("select DISTINCT(idclt),client.nom,client.mail,client.tel,client.ice  from client join commande using(idclt) where nocmd is not null");
            while(rs.next()){
                CbxClient.addItem(new Client(rs.getInt(1),rs.getString(2),rs.getString(3),rs.getString(4), rs.getInt(5)));
            }
        } catch (Exception ex) {
            Logger.getLogger(JiPaiment.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_formInternalFrameOpened

    private void CbxClientItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_CbxClientItemStateChanged
        try {
            // TODO add your handling code here:
            String req="select sum(total),sum(depo),sum((total-depo)) from paiment where idclt=?";
            Client c=(Client) CbxClient.getSelectedItem();
            Connection con=Connect.seconnenter();
            PreparedStatement ps=con.prepareStatement(req);
            ps.setInt(1, c.getId_clt());
            ResultSet rs=ps.executeQuery();
            if(rs.next()){
                labID.setText(c.getId_clt()+"");
                LabNom.setText(c.getNom());
                LabMail.setText(c.getMail());
                LabTel.setText(c.getTel());
                LabTotal.setText(rs.getFloat(1)+"");
                labDepo.setText(rs.getFloat(2)+"");
                LabCredit.setText(rs.getFloat(3)+"");
                
            }
        } catch (Exception ex) {
            Logger.getLogger(JiPaiment.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_CbxClientItemStateChanged

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            // TODO add your handling code here:
            int noclt=Integer.parseInt(labID.getText());
            JasperDesign jasperdesign=JRXmlLoader.load("src//Report//GestionPaiment.jrxml");
            JasperReport jasperReport=JasperCompileManager.compileReport(jasperdesign);
            Map parametres =new HashMap();
            parametres.put("idclt", noclt);
            JasperPrint jasperprint=JasperFillManager.fillReport(jasperReport, parametres,Connect.seconnenter());
            JRViewer jv=new JRViewer(jasperprint);
            Impre v=new Impre();
            v.setVisible(true);
            v.getContentPane().add(jv);
        } catch (JRException ex) {
            Logger.getLogger(JiPaiment.class.getName()).log(Level.SEVERE, null, ex);
        } catch (Exception ex) {
            Logger.getLogger(JiPaiment.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_jButton1ActionPerformed


